import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [enteredShowForm, setenteredShowForm] = useState(false);

  //   const [userInput, setUserInput] = useState({
  //     enteredTitle: "",
  //     enteredAmount: "",
  //     enteredDate: "",
  //   });

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    // setUserInput({
    //     //Using a spreader might fail in certain scenarios thus,
    //     //might not be such a good solution for this use case.
    //     ...userInput,
    //     enteredTitle: event.target.value,
    // })
    // setUserInput((prevState) => {
    //     return {...prevState, enteredTitle: event.target.value}
    // })
  };
  //Whenever I'm updateing the state and is depending on the previous
  //state; pass in a function in the setUserInput

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    // setUserInput({
    //     ...userInput,
    //     enteredAmount: event.target.value,
    // })
    //If the state depends on the previous state, use this syntax.
    //Otherwise, if there are many values, some might be forgotten.
    // setUserInput((prevState) => {
    //     return {...prevState, enteredAmount: event.target.value}
    // })
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
    // setUserInput({
    //     ...userInput,
    //     enteredDate: event.target.value,
    // })
  };
  const submitHandler = (event) => {
    event.preventDefault();
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    // console.log(expenseData)
    //Here we reset the values after they have been submitted
    //with the value and function in the JSX, it is called
    //two way binding!!
    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };

  const showExpenseForm = () => {
    console.log("expenseForm is clicked");
    if (enteredShowForm === true) {
      setenteredShowForm(false);
      console.log(enteredShowForm);
      return <h1>Not show</h1>;
    } else {
      setenteredShowForm(true);
      console.log(enteredShowForm);
      return <h1>SHow much</h1>;
    }
  };

  let showForm = <form onSubmit={submitHandler}>
    <div className="new-expense__actions">
        <button onClick={showExpenseForm}>Cancel</button>
      </div>
  </form>
  if (enteredShowForm){
  showForm = (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
            value={enteredAmount}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            value={enteredDate}
            min="2019-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button onClick={showExpenseForm}>Cancel</button>
        <button type="submit">Add expense</button>
      </div>
    </form>
  )};

  return showForm;
};

export default ExpenseForm;
